def replace(frases):
    global data
    for frase in frases:
        data = data.replace(frase, f'{frase}\n')


def getName(name):
    name = name.strip()
    for i, letra in enumerate(name):
        if letra == '(':
            name = name[i+1:-1]
    return name


def getTime(time):
    time = time.split('confirmación')
    time = time[0] if len(time) == 1 else time[1]
    time = time.split('confirmaciones')
    time = time[0] if len(time) == 1 else time[1]
    time = time.split('N/A')
    time = time[0] if len(time) == 1 else time[1]
    time = time.strip()
    if time == 'Casi al instante':
        return 0
    n = ''
    tipo = time.split(' ')
    if tipo[-1] == 'minutos' or tipo[-1] == 'minuto':
        tipo = 1
    elif tipo[-1] == 'segundos' or tipo[-1] == 'segundo':
        tipo = 0
    elif tipo[-1] == 'horas' or tipo[-1] == 'hora':
        tipo = 2
    elif tipo[-1] == 'días':
        tipo = 3
    for letra in time:
        if letra.isdigit():
            n += letra
    if n == '':
        return '-'
    return int(n)*(60**tipo)


