replace(frases)
data = data.split('\n')
data = set(data)
data.remove('')
data = list(data)
for i, coin in enumerate(data):
    if coin == '':
        continue
    aux = coin.split('\t')
    name = getName(aux[0])
    time = getTime(aux[2] if len(aux) == 3 else aux[1])
    data[i] = {'name': name, 'time': time}

diccionario = dict()

for coin in data:
    diccionario.update({coin['name']: coin['time']})
print(diccionario)
